import re
from collections import Counter

def top_3_words(text):
    print(text)
    words = re.split(r"[ \-/,;:.!?_]+", text.lower())
    while "" in words:
        words.remove("")
    for word in words:
        if word.count("\'") == len(word):
            words.remove(word)
    dict_words = Counter(words)
    top_3 = dict_words.most_common(3)
    return [word[0] for word in top_3]

